#!/usr/bin/env python3

class Note:
    def __init__(self, title, text, encodedImage):
      self.title = title
      self.text = text
      self.encodedImage = encodedImage

    def getString(self):
      return {'title': self.title, 'text': self.text, 'encodedImage' : self.encodedImage}

from flask import Flask, render_template, jsonify, json, request
from flask_sock import Sock
from simple_websocket.ws import ConnectionClosed


image = ""
notes = []
note_test = Note("Mercadona", "Sabonete", image)
notes.append(note_test.getString())
note_test = Note("Escola", "papel", image)
notes.append(note_test.getString())
note_test = Note("Bombeiros", "mangueira", image)
notes.append(note_test.getString())
note_test = Note("Biblia", "TOP-G", image)
notes.append(note_test.getString())

websocket_connections = []



def add_note(new_note):
  global notes
  notes.append(new_note)
  websocket_broadcast(notes)


def websocket_broadcast(notes):
  for ws in websocket_connections:
    try:
      ns = json.dumps(notes)
      print("Vou fazer broadcast de : " + str(ns))
      ws.send(ns)
    except ConnectionClosed:
      websocket_connections.remove(ws)


app = Flask(__name__)
sockets = Sock(app)


@app.route('/', methods=['GET'])
def index():
  return render_template('index.html')


@app.route('/notes', methods=['GET'])
def get_notes():
  global notes
  print("Recebi um pedido de /notes")
  n = jsonify(notes)
  print("Vou enviar estas notes : " +  str(n))
  return n


@app.route('/notes', methods = ['PUT'])
def put_state():
  global notes
  n = json.loads(request.data)
  print(n)
  print("Recebi a note : " + str(n))
  add_note(n)
  ns = jsonify(notes)
  print("Vou dar returns das notes actualizadas : " + str(ns) )
  return ns

@sockets.route('/ws')
def ws(ws):
  global websocket_connections, notes
  websocket_connections.append(ws)

  while True:
    jDump = json.dumps(notes)
    print(jDump)
    ws.send(json.dumps(notes))
    ws.receive()
    print("DDDDDDDDDDDDDDDD")


from gevent import monkey
monkey.patch_all()
from gevent.pywsgi import WSGIServer
WSGIServer(('0.0.0.0', 5000), app).serve_forever()
